SELECT ar.artist_name AS artista, CASE WHEN COUNT(fa.favorite_id) >= 5 THEN 'A' WHEN COUNT(fa.favorite_id) >= 3 THEN 'B' WHEN COUNT(fa.favorite_id) >= 1 THEN 'C' ELSE '-' END AS ranking FROM SpotifyClone.artists AS ar JOIN SpotifyClone.albums AS al ON ar.artist_id = al.artist_id JOIN SpotifyClone.songs AS so ON al.album_id = so.album_id LEFT JOIN SpotifyClone.favorites AS fa ON so.song_id = fa.song_id GROUP BY ar.artist_id ORDER BY COUNT(fa.favorite_id) DESC, ar.artist_name;