SELECT ar.artist_name AS `artista`, al.album_name AS `album`, COUNT(DISTINCT fo.user_id) AS `pessoas_seguidoras` FROM SpotifyClone.artists AS ar INNER JOIN SpotifyClone.albums AS al ON ar.artist_id = al.artist_id INNER JOIN SpotifyClone.following AS fo ON ar.artist_id = fo.artist_id GROUP BY ar.artist_name, al.album_name ORDER BY `pessoas_seguidoras` DESC, ar.artist_name, al.album_name;